<?php session_start();require_once'vendor/autoload.php';$dotenv=Dotenv\Dotenv::createImmutable(__DIR__);$dotenv->load();if(!isset($_SESSION['user_id'])){header("Location:login.php");exit();}try{$conn=new mysqli($_ENV['DB_HOST'],$_ENV['DB_USER'],$_ENV['DB_PASS'],$_ENV['DB_NAME']);if($conn->connect_error){throw new Exception("خطا در اتصال به دیتابیس");}$conn->set_charset("utf8mb4");$user_id=$_SESSION['user_uid']??'all';$username=$_SESSION['user_name']??null;$lessons=range(1,12);$locked_lessons=array_fill(1,12,true);for($i=6;$i<=12;$i++){$locked_lessons[$i]=true;}if($user_id!='all'){$stmt=$conn->prepare("SELECT lesson_id,is_locked FROM lesson_locks WHERE user_id=?");$stmt->bind_param("s",$user_id);$stmt->execute();$result=$stmt->get_result();while($row=$result->fetch_assoc()){$locked_lessons[$row['lesson_id']]=$row['is_locked']==1;}}foreach([1,2,3,4,5]as$open_lesson){$locked_lessons[$open_lesson]=false;}}catch(Exception$e){error_log($e->getMessage());die("خطا در اتصال به دیتابیس");}$course_videos=['https://gold24.io/English/c3e4c3145f8a1374bb5511328d8dd7d9.MP4','https://gold24.io/English/99d975b681b3580821ffe35b3c84c34c.MP4','https://gold24.io/English/e7942b24f73989fd6e94ed66f13a5245.MP4','https://gold24.io/English/b78f5bdc712232920a030900a6fc65e2.MP4','https://gold24.io/English/cb509dd51fb54ae76a5eba0c22eceab4.MP4','https://gold24.io/English/386d485b6d91d435dad096967bd50b50.MP4','https://gold24.io/English/49e718c4cd215b584bed8e012006924c.MP4','https://gold24.io/English/5b7e503f907c2a099dfda791f3267182.MP4','https://gold24.io/English/4fdeb4bdf50a5e94a8e97a01b4ec172a.MP4','https://gold24.io/English/21176fb17cb0029baf3930187eed0b4b.MP4','https://gold24.io/English/8e9bf54acbf767eb63e736d1dd91681f.MP4','https://gold24.io/English/1e15e59f08db4a9e97e9a0a45f7830f1.MP4'];?><!DOCTYPE html><html lang="fa"dir="rtl"><head><meta charset="UTF-8"><meta name="viewport"content="width=device-width,initial-scale=1.0"><title>آموزش زبان انگلیسی|English.Online24</title><meta name="descripti