<?php session_start(); error_reporting(E_ALL); ini_set('display_errors', 1); if (!isset($_SESSION['user_uid'])) { header("Location: login.php"); exit; } $servername = "localhost"; $username = "gold24_user"; $password = "random_password"; $dbname = "gold24_db"; $conn = new mysqli($servername, $username, $password, $dbname); if ($conn->connect_error) { die("Connection failed: " . $conn->connect_error); } $conn->set_charset("utf8mb4"); $user_uid = $_SESSION['user_uid']; $message = ''; $message_type = ''; if ($_SERVER['REQUEST_METHOD'] == 'POST') { $current_password = trim($_POST['current_password']); $new_password = trim($_POST['new_password']); $confirm_password = trim($_POST['confirm_password']); if ($new_password !== $confirm_password) { $_SESSION['message'] = 'رمزهای عبور جدید با یکدیگر مطابقت ندارند'; $_SESSION['message_type'] = 'error'; header("Location: " . $_SERVER['PHP_SELF']); exit(); } $stmt = $conn->prepare("SELECT password FROM users WHERE user_uid = ?"); $stmt->bind_param("s", $user_uid); $stmt->execute(); $result = $stmt->get_result(); if ($result->num_rows > 0) { $hashed_password = $result->fetch_assoc()['password']; if (password_verify($current_password, $hashed_password)) { $new_hashed_password = password_hash($new_password, PASSWORD_DEFAULT); $update_stmt = $conn->prepare("UPDATE users SET password = ? WHERE user_uid = ?"); $update_stmt->bind_param("ss", $new_hashed_password, $user_uid); if ($update_stmt->execute()) { $_SESSION['message'] = 'رمز عبور با موفقیت تغییر کرد'; $_SESSION['message_type'] = 'success'; } else { $_SESSION['message'] = 'خطا در بروزرسانی رمز عبور'; $_SESSION['message_type'] = 'error'; } $update_stmt->close(); } else { $_SESSION['message'] = 'رمز عبور فعلی اشتباه است'; $_SESSION['message_type'] = 'error'; } } $stmt->close(); header("Location: " . $_SERVER['PHP_SELF']); exit(); } if (isset($_SESSION['message'])) { $message = $_SESSION['message']; $message_type = $_SESSION['message_type']; unset($_SESSION['message']); unset($_SESSION['message_type']); } $conn->close(); ?><!DOCTYPE html><html lang="fa" dir="rtl"><head><meta charset="UTF-8"><meta name="viewport" content="width=device-width, initial-scale=1.0"><title>تغییر رمز عبور</title><style>:root{--dark-brown:#3C2A21;--medium-brown:#8B6B44;--light-brown:#D9B88C;--gold:#FFD700;--cream:#F5E6D3;--text-light:#FFF;--text-dark:#2C1810}*{box-sizing:border-box;margin:0;padding:0}body{background:linear-gradient(135deg,var(--dark-brown),var(--medium-brown));font-family:'Vazirmatn',sans-serif;min-height:100vh;margin:0;padding:20px;color:var(--text-light);display:flex;justify-content:center;align-items:center}.profile-container{background:linear-gradient(to bottom right,var(--medium-brown),var(--light-brown));width:90%;max-width:500px;border-radius:15px;padding:30px;box-shadow:0 10px 30px rgba(0,0,0,0.3);position:relative}.profile-container::before{content:'';position:absolute;top:0;left:0;right:0;height:5px;background:linear-gradient(to right,var(--gold),var(--light-brown))}.notification{padding:15px;margin:20px 0;border-radius:8px;background:rgba(255,255,255,0.1);border:1px solid var(--gold)}.notification.success{background:rgba(21,87,36,0.9);color:var(--gold)}.notification.error{background:rgba(114,28,36,0.9);color:var(--gold)}.header{text-align:center;margin-bottom:30px}h2{color:var(--gold);text-align:center;margin-bottom:30px}.form-group{margin-bottom:20px}.form-group label{display:block;color:var(--gold);margin-bottom:8px;font-size:14px}.form-group input{width:100%;padding:12px;background:rgba(255,255,255,0.1);border:1px solid var(--light-brown);border-radius:8px;color:var(--text-light);font-size:15px}.form-group input:focus{outline:none;border-color:var(--gold);box-shadow:0 0 10px rgba(255,215,0,0.2)}.button-container{display:flex;gap:15px;margin-top:30px}.btn{flex:1;padding:12px;border:none;border-radius:8px;font-size:15px;font-weight:bold;cursor:pointer;transition:0.3s;text-align:center;text-decoration:none}.save-btn{background:linear-gradient(45deg,var(--gold),var(--light-brown));color:var(--text-dark)}.save-btn:hover{transform:translateY(-2px)}.back-btn{background:rgba(255,255,255,0.1);color:var(--cream);border:1px solid var(--cream)}.back-btn:hover{background:rgba(255,255,255,0.2)}@media (max-width:480px){.profile-container{padding:20px}.button-container{flex-direction:column}.btn{width:100%;margin-bottom:10px}}</style></head><body><div class="profile-container"><h2>تغییر رمز عبور</h2><?php if(!empty($message)):?><div class="notification <?php echo $message_type;?>"><?php echo $message;?></div><?php endif;?><form method="POST" action=""><div class="form-group"><label for="current-password">رمز عبور فعلی:</label><input type="password" id="current-password" name="current_password" required></div><div class="form-group"><label for="new-password">رمز عبور جدید:</label><input type="password" id="new-password" name="new_password" required></div><div class="form-group"><label for="confirm-password">تکرار رمز عبور جدید:</label><input type="password" id="confirm-password" name="confirm_password" required></div><div class="button-container"><button type="submit" class="btn save-btn">ذخیره تغییرات</button><a href="/English/index.php" class="btn back-btn">بازگشت به خانه</a></div></form></div></body></html>
